window.addEventListener("load",function(){var e=document.querySelector("#div-Img"),t=document.querySelector("#div-ImgCats"),n=document.querySelector("#div-ImgFox"),o=document.querySelector("#btn-RandomDog"),d=document.querySelector("#btn-RandomCats"),c=document.querySelector("#btn-RandomFox");let i=()=>fetch("https://dog.ceo/api/breeds/image/random"),r=()=>fetch("https://aws.random.cat/meow"),a=()=>fetch("https://randomfox.ca/floof/");o.addEventListener("click",function(){i().then(e=>e.json()).then(t=>{let n=document.querySelector("#img-Dogs");n.src=t.message,n.width="200",n.height="200",e.appendChild(n)})}),d.addEventListener("click",function(){r().then(e=>e.json()).then(e=>{let n=document.querySelector("#img-Cats");n.src=e.file,n.width="200",n.height="200",t.appendChild(n)})}),c.addEventListener("click",function(){a().then(e=>e.json()).then(e=>{let t=document.querySelector("#img-Fox");t.src=e.image,t.width="200",t.height="200",n.appendChild(t)})})});